import React from 'react'

const Quiz = ({ quiz }) => {

    const perguntas = quiz.perguntas

    if (typeof(perguntas) === 'undefined') {return (<div>carregando...</div>)}

    const listPerguntas = perguntas.map( (pergunta) =>
        <div>
            <h5>{pergunta.description}</h5>
            <form name={pergunta.description}>
                {pergunta.alternatives.map( (alternatives) => 
                    <label>
                        <input type="radio" value={alternatives._id} name={pergunta.description}></input>
                        <p>{alternatives.text}</p>
                    </label>
                )}
            </form>
        </div>
    )

        return (
            <div>
                <h1>{quiz.description}</h1>
                {listPerguntas}
            </div>
        ); 
    };

export default Quiz
