import React from 'react'

const Quiz = ({ quiz }) => {

    const perguntas = quiz.perguntas

    if (typeof(perguntas) === 'undefined') {return (<div>carregando...</div>)}

    const listPerguntas = perguntas.map( (pergunta) =>
        <div class="card mt-2 mb-2">
            <div class="card-body">
                <h5 class="card-title">{pergunta.description}</h5>
                <ol class="list-group list-group-numbered">
                    {pergunta.alternatives.map( (alternatives) => 
                        <li class="list-group-item"> {alternatives.text} </li>)}
                </ol>
            </div>
        </div>
    )

        return (
            <div>
                <h1>{quiz.description}</h1>
                {listPerguntas}
            </div>
        ); 
    };

export default Quiz
