    constructor() {
        super();
        this.state = {
            quiz: [],
            results: []
        };
    };

    childSetState = item => {
        this.setState({results: item})
        console.log("State.results = ", this.state.results)
    }

    childGetResults = () => {
        return this.state.results
    }

    componentDidMount() {
        fetch('http://localhost:8000/quiz/60a1429417321c25f43be291')
            .then(res => res.json())
            .then((data) => {
                this.setState({ quiz: data})
            })
