class Quiz extends React.Component {
    constructor(props) {
        super(props);
        this.state = {questions: [], results: []}
    }

    componentDidMount() {
    }

    onSubmit(){}

    renderQuestions() {
        const questions = this.props.quiz.perguntas

        if (typeof(questions) === 'undefined') {return (<div>carregando...</div>)}
        console.log(questions)

        //questions.map( (question, index) => {return (<h5> {question.description} </h5>) } )
        questions.map( (question, index) => {return <h2>{question.description}</h2>  } )
        //return <h2>renderQuestions()</h2>
    }

    renderQuiz() {
        const description = this.props.quiz.description
        
        if (typeof(description) === 'undefined') {return (<div>carregando...</div>)}

        return(
            <h1>{description}</h1>
        )
    }


    render() {
        return(
            <div>
                {this.renderQuiz()}
                {this.renderQuestions()}
            </div>
        )
    }
}

export default Quiz
