import React, {Component} from 'react';
import Perguntas from './components/perguntas'

class App extends Component {
    state = {
        perguntas: []
    }

    format_data(data) {
        let formated_data = [];
        let i;

        for (i = 0; i < data.length; i++) {
            console.log("i = ", i)
            console.log("data[i] ", data[i])
            formated_data.push(data[i])
        };

        console.log("Og: ", data)
        console.log("Og.len: ", data.length)
        console.log("Fmt: ", formated_data)

        return formated_data
    }

    componentDidMount() {
        fetch('http://localhost:8000/perguntas')
        .then(res => res.json())
        .then((data) => {
            //this.setState({ perguntas: data })
            this.setState( { perguntas: this.format_data(data) }
                )
        })
        .catch(console.log)
    }

    componentDidUpdate(prop) {
        //console.log("this.state.perguntas[0][0]", this.state.perguntas[0][0])
        console.log("this.state.perguntas", this.state.perguntas)
    }

    render() {
        return (
            //<Perguntas perguntas={this.state.perguntas} />
            null
        )
    }
}

export default App;
