import React, {Component} from 'react';
import Perguntas from './components/perguntas'

class App extends Component {
    state = {
        perguntas: []
    }

    format_data(data) {
        let formated_data = [];
        let i;

        for (i = 0, i < data.length; i++;) {
            formated_data[i] = data[i]
        };

        return formated_data
    }

    componentDidMount() {
        fetch('http://localhost:8000/perguntas')
        .then(res => res.json())
        .then((data) => {
            //this.setState({ perguntas: data })
            this.setState( { perguntas: this.format_data(data) }
                )
        })
        .catch(console.log)
    }

    componentDidUpdate(prop) {
        //console.log("this.state.perguntas[0][0]", this.state.perguntas[0][0])
        console.log("this.state.perguntas[0]", this.state.perguntas[0])
    }

    render() {
        return (
            <Perguntas perguntas={this.state.perguntas} />
            //null
        )
    }
}

export default App;
