import React from 'react'

class Quiz extends React.Component {
    constructor(props) {
        super(props);
        this.state = {questions: [], results: []}
        this.data = {}
    }

    componentDidMount() {
    }

    onSubmit = async e => {
        e.preventDefault();
        console.log(this.data)
    }

    handleChange = e => {
        let key = e.target.name.toString()
        let value = e.target.value.toString()
        this.data[key] = value
    }

    renderDescription() {
        const description = this.props.quiz.description
        
        if (typeof(description) === 'undefined') {return (<div>carregando...</div>)}

        return(
            <h1>{description}</h1>
        )
    }

    renderQuestions() {
        const questions = this.props.quiz.perguntas

        if (typeof(questions) === 'undefined') {return (<div>carregando...</div>)}

        return (
            <div>
            {   questions.map( (question, index) => (
                    <h3 key={index}> {question.description} {this.renderAlternatives(question._id, question.alternatives)} </h3> 
                )) 
            } 
            </div>
        )
    }

    renderAlternatives(questionId,alternatives) {
        //console.log(questionId)
        return(
            <div>
                {
                    alternatives.map( (alternative, index) => (
                        <div key={index}>
                            <h5> <input type="radio" name={questionId} value={alternative._id}  onChange={this.handleChange}></input> {alternative.text} </h5>
                        </div>
                    ))
                }
            </div>
        )
    }

    render() {
        return(
            <form onSubmit={this.onSubmit}>
                <div>
                    {this.renderDescription()}
                    {this.renderQuestions()}
                    <button>Enviar</button>
                </div>
            </form>
        )
    }
}

export default Quiz
