import React from 'react'

const Quiz = ({ quiz, parentSetState, parentGetResults }) => {

    const formData = {}

    const onSubmit = async e => {
        e.preventDefault();
        let promised_response;

        await fetch('http://localhost:8000/responder', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(formData),
        })
        //.then(response => { parentSetState(response.json()); })
        .then(response => { promised_response = response.json(); console.log("promised response: ", promised_response) })
        .then(parentSetState(promised_response))

        console.log("parent results: ", parentGetResults())
    }

    const handleChange = event => {
        formData[event.target.name.toString()] = event.target.value.toString()
    }

    const perguntas = quiz.perguntas
    if (typeof(perguntas) === 'undefined') {return (<div>carregando...</div>)}

    const results = parentGetResults();
    console.log("Parent results: ", results)

    const listPerguntas = perguntas.map( (pergunta) =>
        <div>
            <h5>{pergunta.description}</h5>
                {pergunta.alternatives.map( (alternatives) => 
                    <label>
                        <input type="radio" name={pergunta._id} value={alternatives._id} onChange={handleChange}></input>
                        {alternatives.text}
                    </label>
                )}
        </div>
    );

    const listResults = perguntas.map( (pergunta) =>
        <div>
            <h5>{pergunta.description} RESULTADOS</h5>
                {pergunta.alternatives.map( (alternatives) => 
                    <label>
                        <input type="radio" name={pergunta._id} value={alternatives._id} onChange={handleChange}></input>
                        {alternatives.text}
                        <h2>{results[alternatives._id] == undefined ? "carregando..." : results[alternatives._id].toString()}</h2>
                    </label>
                )}
        </div>
    );
    

    //if (results.length === 0) {
    console.log("keys: ", Object.keys(results))
    if (Object.keys(results).length == 0) {
        return (
            <div>
                <h1>{quiz.description}</h1>
                <form onSubmit={onSubmit}>
                    {listPerguntas}
                    <button>Enviar</button>
                </form>
            </div>
        );
    } 
    else {
        return (
            <div>
                <h1>{quiz.description}</h1>
                <form onSubmit={onSubmit}>
                    {listResults}
                    <button>Enviar</button>
                </form>
            </div>
        );
    }

};

export default Quiz
