import React from 'react'

class Quiz extends React.Component {
    constructor(props) {
        super(props);
        this.state = {questions: [], results: []}
    }

    componentDidMount() {
    }

    onSubmit(){}

    renderDescription() {
        const description = this.props.quiz.description
        
        if (typeof(description) === 'undefined') {return (<div>carregando...</div>)}

        return(
            <h1>{description}</h1>
        )
    }

    renderQuestions() {
        const questions = this.props.quiz.perguntas
        console.log(questions)

        if (typeof(questions) === 'undefined') {return (<div>carregando...</div>)}

        return (
             <div>{ questions.map( (question, index) => (
                <h3 key={index}> {question.description} </h3> )) } 
             </div>
        )
    }

    renderAlternatives(alternatives) {
        console.log(alternatives)
    }

    render() {
        return(
            <div>
                {this.renderDescription()}
                {this.renderQuestions()}
            </div>
        )
    }
}

export default Quiz
