import React from 'react'

const Perguntas = ({ perguntas }) => {

    const listAlternatives = perguntas.map( (pergunta) =>
    
            pergunta.alternatives.map( (alternatives) =>  
                <p class="card-text">{alternatives.text}</p> 
            )
        )

    const listPerguntas = perguntas.map( (pergunta) =>
            <div class="card">
                <div class="card-body">
                    <h5 class="card-title">{pergunta.description}</h5>
                    {listAlternatives}
                </div>
            </div>
        )

        return (
            <div><h1>Perguntas disponÃ­veis</h1>
                {listPerguntas}
            </div>
        ); 
    };

export default Perguntas
