import React from 'react'

class Quiz extends React.Component {
    constructor(props) {
        super(props);
        this.state = {questions: [], results: []}
        this.data = {}
        this.results = {}
    }

    componentDidMount() {
    }

    onSubmit = async e => {
        e.preventDefault();

        await fetch('http://localhost:8000/responder', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(this.data),
        })
        .then(response => { this.setState({results: response}) })
        .catch( error => {console.log(error)})
    }

    handleChange = e => {
        const key = e.target.name.toString()
        const value = e.target.value.toString()
        this.data[key] = value
    }

    renderDescription() {
        const description = this.props.quiz.description
        
        if (typeof(description) === 'undefined') {return (<div>carregando...</div>)}

